function BeerSales (customername,typeofbeer,images,amount,pricebeer,date){
    this.customername = customername;
    this.typeofbeer = typeofbeer;
    // this.pricebeerVND = pricebeerVND;
    this.images = images;
    this.amount = amount;
    this.pricebeer = pricebeer;
    this.date = date;
}
const beersaleskey = "Beersaleskey"
let beer = [ 
    new BeerSales ("Nguyễn Văn A", "Huda","data:image/jpeg;base64,",10, 12000, "2020-01-01"),
    new BeerSales ("Nguyễn Văn B", "larue","data:image/jpeg;base64,",15, 20000, "2022-01-01"),
    new BeerSales ("Nguyễn Văn C", "Heineken","data:image/jpeg;base64,",15, 20000, "2022-01-01"),
]
let listBeer = [];
if(localStorage.getItem(beersaleskey) == undefined){
    listBeer = beer;
    localStorage.setItem(beersaleskey, JSON.stringify(listBeer));
} else {
    listBeer = JSON.parse(localStorage.getItem(beersaleskey));
}
function renderBeerSales () {
    let tbbody = document.querySelector("table>tbody");
    let htmls = listBeer.map(function(value,index){
        // let totalmoney = formatCurency(value.amount*value.pricebeer);
        // value.pricebeerVND = formatCurency(value.pricebeer);
        return `
        <tr>
        <td id="customername${index}">${value.customername}</td>
        <td id="typeofbeer${index}">${value.typeofbeer}</td>
        <td id="images${index}"><img class="img-sm" src="${value.images}" alt=""></td>
        <td id="amount${index}">${value.amount}</td>
        <td id="price${index}">${formatCurency(value.pricebeer)}</td>
        <td id="date${index}">${value.date}</td>
        <td>
            <a><button class="icon" onclick="editBeerSales(${index})"><i class="fas fa-edit"></i></button></a>
            <a><button class="icon" onclick="deleteBeerSales(${index})"> <i class="fas fa-trash"></i></button></a>       

        </td>
        <td>${formatCurency(value.amount*value.pricebeer)}</td>
    </tr>        
        `
    })
    tbbody.innerHTML = htmls.join("");
}
renderBeerSales();

function formatCurency (pricebeerVND) {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(pricebeerVND);
}

function addToCart() {
    const newBeer = new BeerSales ();
    newBeer.customername = document.querySelector("#customername").value;
    newBeer.typeofbeer = document.querySelector("#typeofbeer").value;
    newBeer.pricebeer = document.querySelector("#price").value;
    newBeer.amount = document.querySelector("#amount").value;
    newBeer.date = document.querySelector("#date").value;
    newBeer.images = document.getElementById("images").value;
    listBeer.unshift(newBeer);
    //loadFile();
    renderBeerSales();
    document.querySelector("#myForm").reset();
    localStorage.setItem(beersaleskey, JSON.stringify(listBeer));
    
    
}

function deleteBeerSales (index) {
    let confirmed = window.confirm("Are you to remove this type of beer? ")
    if (confirmed) {      
            listBeer.splice(index,1);

        
    }
    renderBeerSales();
    localStorage.setItem(beersaleskey, JSON.stringify(listBeer)); }

function editBeerSales(index) {
    document.querySelector('#customername').value = listBeer[index].customername;
    document.querySelector('#typeofbeer').value = listBeer[index].typeofbeer;
    document.querySelector('#price').value = listBeer[index].pricebeer;
    document.querySelector('#amount').value = listBeer[index].amount;
    document.querySelector('#date').value =  listBeer[index].date; //yyyy-mm-dd
    // document.querySelector('#images').src = `<img class="img-sm" src="${beer.images}" alt="">`;
    document.querySelector('#images').value = listBeer[index].images;
    document.querySelector('#index').value = index;

    document.querySelector('#save').style.display = "none";
    document.querySelector('#update').style.display = "inline-block";

    localStorage.setItem(beersaleskey, JSON.stringify(listBeer));
    renderBeerSales();

}
function changeBeer() {
    let index =document.querySelector('#index').value;
    listBeer[index] =  {
        customername: document.querySelector('#customername').value,
        typeofbeer: document.querySelector('#typeofbeer').value,
        pricebeer: document.querySelector('#price').value,
        amount: document.querySelector('#amount').value,
        date: document.querySelector('#date').value,
        images: document.querySelector('#images').value
    }
    console.log(listBeer[index]);
    localStorage.setItem(beersaleskey, JSON.stringify(listBeer));
    renderBeerSales();
    
    document.querySelector('#save').style.display = "inline-block";
    document.querySelector('#update').style.display = "none";
}